import React from 'react';
import { Link } from 'react-router-dom';
import hotels from '../data/hotels.json';

function Home() {
  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Hotel List</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {hotels.map(hotel => (
          <Link
            key={hotel.id}
            to={`/hotel/${hotel.id}`}
            className="border p-4 rounded shadow hover:bg-gray-100"
          >
            <h2 className="font-semibold">{hotel.name}</h2>
            <p>{hotel.location}</p>
            <p>‚≠ê {hotel.rating}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Home;
