import React, { useEffect, useState } from "react";
import HotelCard from "./HotelCard";
import hotelsData from "../data/hotels.json";

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedStar, setSelectedStar] = useState("All Stars");
  const [showRecommended, setShowRecommended] = useState(false);
  const [filteredHotels, setFilteredHotels] = useState([]);

  useEffect(() => {
    let hotels = hotelsData;

    // 🔍 Search filter
    if (searchTerm.trim()) {
      hotels = hotels.filter((hotel) =>
        hotel.hotel_name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // 🏙️ City filter
    if (selectedCity) {
      hotels = hotels.filter(
        (hotel) => hotel.city.toLowerCase() === selectedCity.toLowerCase()
      );
    }

    // ⭐ Star Rating filter (by .star_rating not rating_average)
    if (selectedStar !== "All Stars") {
      const star = parseInt(selectedStar);
      hotels = hotels.filter((hotel) => Math.round(hotel.star_rating) === star);
    }

    // 🏆 Recommended filter (by rating_average)
    if (showRecommended) {
      hotels = hotels.filter((hotel) => hotel.rating_average >= 8);
    }

    setFilteredHotels(hotels);
  }, [searchTerm, selectedCity, selectedStar, showRecommended]);

  // Unique cities for dropdown
  const cityOptions = [...new Set(hotelsData.map((hotel) => hotel.city))];

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Explore Hotels</h1>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          className="p-2 border rounded w-60"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <select
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">All Cities</option>
          {cityOptions.map((city) => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedStar}
          onChange={(e) => setSelectedStar(e.target.value)}
          className="p-2 border rounded"
        >
          <option>All Stars</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </select>

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={showRecommended}
            onChange={() => setShowRecommended(!showRecommended)}
          />
          Show Recommended
        </label>
      </div>

      {/* Hotel Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredHotels.map((hotel) => (
          <HotelCard key={hotel.hotel_id} hotel={hotel} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
