import React, { useEffect, useState } from "react";
import hotelsData from "../data/hotels.json";
import HotelCard from "./HotelCard";

const HomePage = () => {
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    setHotels(hotelsData);
  }, []);

  const topRatedHotels = hotels.filter((hotel) => hotel.star_rating >= 4.5);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Explore Hotels</h1>

      {/* Top Rated Section */}
      <div className="mb-10">
        <h2 className="text-2xl font-semibold mb-4 text-green-700">
          ‚≠ê Top Rated Hotels
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {topRatedHotels.length > 0 ? (
            topRatedHotels.map((hotel) => (
              <HotelCard key={hotel.hotel_id} hotel={hotel} />
            ))
          ) : (
            <p>No top-rated hotels found.</p>
          )}
        </div>
      </div>

      {/* All Hotels Section */}
      <div>
        <h2 className="text-2xl font-semibold mb-4">All Hotels</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {hotels.map((hotel) => (
            <HotelCard key={hotel.hotel_id} hotel={hotel} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
