import React, { useEffect, useState } from "react";
import hotelsData from "../data/hotels.json";
import HotelCard from "./HotelCard";

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [filteredHotels, setFilteredHotels] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCity, setSelectedCity] = useState("All");
  const [selectedRating, setSelectedRating] = useState("All");
  const [recommendedOnly, setRecommendedOnly] = useState(false);
  const [visited, setVisited] = useState([]);

  useEffect(() => {
    setHotels(hotelsData);
  }, []);

  useEffect(() => {
    applyFilters();
  }, [searchQuery, selectedCity, selectedRating, recommendedOnly, hotels]);

  const handleVisit = (hotel) => {
    const newVisited = [...visited, hotel];
    setVisited(newVisited);
    localStorage.setItem("visited", JSON.stringify(newVisited));
  };

  const applyFilters = () => {
    let filtered = [...hotels];

    if (searchQuery.trim() !== "") {
      filtered = filtered.filter(hotel =>
        hotel.hotel_name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (selectedCity && selectedCity !== "All") {
      filtered = filtered.filter(hotel => hotel.city === selectedCity);
    }

    if (selectedRating && selectedRating !== "All") {
      filtered = filtered.filter(hotel => hotel.star_rating >= parseFloat(selectedRating));
    }

    if (recommendedOnly) {
      filtered = filtered.filter(hotel => hotel.star_rating >= 4 && hotel.rates_from < 100);
    }

    setFilteredHotels(filtered);
  };

  const cities = [...new Set(hotels.map((hotel) => hotel.city))];

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Explore Hotels</h1>

      {/* Filters */}
      <div className="flex flex-col md:flex-row items-start md:items-center gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border rounded px-3 py-1"
        />

        <select
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          className="border rounded px-3 py-1"
        >
          <option value="All">All Cities</option>
          {cities.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedRating}
          onChange={(e) => setSelectedRating(e.target.value)}
          className="border rounded px-3 py-1"
        >
          <option value="All">All Ratings</option>
          <option value="5">5 Stars</option>
          <option value="4">4+ Stars</option>
          <option value="3">3+ Stars</option>
        </select>

        <label className="flex items-center">
          <input
            type="checkbox"
            checked={recommendedOnly}
            onChange={() => setRecommendedOnly(prev => !prev)}
            className="ml-2"
          />
          <span className="ml-1">Show Recommended</span>
        </label>
      </div>

      {/* Hotel Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredHotels.map((hotel) => (
          <HotelCard key={hotel.hotel_id} hotel={hotel} onVisit={handleVisit} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
