import React, { useState } from 'react';

const HotelCard = ({ hotel }) => {
  const {
    photo1,
    hotel_name,
    city,
    country,
    star_rating,
    rates_from,
    url
  } = hotel;

  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    checkin: "",
    checkout: "",
    rooms: 1
  });

  const handleVisit = () => {
    const visits = JSON.parse(localStorage.getItem('visits')) || {};
    visits[hotel.hotel_id] = (visits[hotel.hotel_id] || 0) + 1;
    localStorage.setItem('visits', JSON.stringify(visits));
    window.open(url, '_blank');
  };

  const handleBookingSubmit = () => {
    const { name, email, phone, checkin, checkout } = formData;

    if (!name || !email || !phone || !checkin || !checkout) {
      alert("Please fill in all fields before proceeding.");
      return;
    }

    const completedBookings = JSON.parse(localStorage.getItem('completedBookings')) || [];
    completedBookings.push({
      ...hotel,
      userDetails: formData,
      timestamp: new Date().toISOString()
    });
    localStorage.setItem('completedBookings', JSON.stringify(completedBookings));

    alert("Booking confirmed! Redirecting to payment gateway...");

    // Redirect to a simulated payment gateway (e.g., Agoda)
    window.open("https://www.agoda.com/en-gb/book/", "_blank");

    setShowModal(false);
    setFormData({
      name: "",
      email: "",
      phone: "",
      checkin: "",
      checkout: "",
      rooms: 1
    });
  };

  return (
    <>
      <div className="bg-white rounded-xl shadow-md overflow-hidden w-[300px] m-4">
        <img
          src={photo1}
          alt={hotel_name}
          className="w-full h-48 object-cover"
          onError={(e) => {
            e.target.src = '/default.jpg';
          }}
        />
        <div className="p-4">
          <h2 className="text-lg font-semibold">{hotel_name}</h2>
          <p className="text-gray-600">{city}, {country}</p>
          <p className="text-yellow-500 mt-1">⭐ {star_rating}</p>
          <p className="text-green-600 font-semibold mt-1">${rates_from} / night</p>

          <div className="mt-4 space-y-2">
            <button
              className="w-full bg-blue-500 text-white py-1 rounded hover:bg-blue-600"
              onClick={handleVisit}
            >
              Visit
            </button>
            <button
              className="w-full bg-green-500 text-white py-1 rounded hover:bg-green-600"
              onClick={() => setShowModal(true)}
            >
              Book
            </button>
          </div>
        </div>
      </div>

      {/* Booking Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg w-96 shadow-lg">
            <h2 className="text-xl font-semibold mb-4">Complete Your Booking</h2>

            <input
              type="text"
              placeholder="Full Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              className="w-full border p-2 mb-2 rounded"
            />
            <input
              type="email"
              placeholder="Email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              className="w-full border p-2 mb-2 rounded"
            />
            <input
              type="tel"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              className="w-full border p-2 mb-2 rounded"
            />
            <input
              type="number"
              placeholder="Number of Rooms"
              min={1}
              value={formData.rooms}
              onChange={(e) => setFormData({ ...formData, rooms: parseInt(e.target.value) })}
              className="w-full border p-2 mb-2 rounded"
            />

            <div className="flex gap-2 mb-2">
              <input
                type="date"
                value={formData.checkin}
                onChange={(e) => setFormData({ ...formData, checkin: e.target.value })}
                className="w-1/2 border p-2 rounded"
              />
              <input
                type="date"
                value={formData.checkout}
                onChange={(e) => setFormData({ ...formData, checkout: e.target.value })}
                className="w-1/2 border p-2 rounded"
              />
            </div>

            <div className="flex justify-end gap-2 mt-4">
              <button
                onClick={() => setShowModal(false)}
                className="px-4 py-2 border rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleBookingSubmit}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Confirm & Pay
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default HotelCard;
