import React from "react";

const HotelCard = ({ hotel }) => {
  return (
    <div className="bg-white rounded-xl shadow-md overflow-hidden w-80">
      <img
        src={hotel.photo1}
        alt={hotel.hotel_name}
        className="w-full h-48 object-cover"
      />
      <div className="p-4">
        <h2 className="text-xl font-semibold">{hotel.hotel_name}</h2>
        <p className="text-gray-600">{hotel.city}, {hotel.country}</p>
        <p className="text-yellow-500">‚≠ê {hotel.star_rating}</p>
        <p className="mt-2 text-green-600 font-bold">${hotel.rates_from} / night</p>
        <a
          href={hotel.url}
          target="_blank"
          rel="noreferrer"
          className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Book Now
        </a>
      </div>
    </div>
  );
};

export default HotelCard;
