import React, { useEffect, useState } from "react";
import hotelData from "../data/hotels.json";

const DraftBookings = () => {
  const [drafts, setDrafts] = useState([]);

  useEffect(() => {
    const rawDrafts = JSON.parse(localStorage.getItem("draftBookings")) || {};
    const allDrafts = [];

    Object.entries(rawDrafts).forEach(([hotelId, bookings]) => {
      const hotel = hotelData.find(h => h.hotel_id === parseInt(hotelId));
      if (hotel) {
        bookings.forEach(booking => {
          allDrafts.push({
            hotelName: hotel.hotel_name,
            city: hotel.city,
            timestamp: booking.timestamp,
          });
        });
      }
    });

    setDrafts(allDrafts);
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Your Draft Bookings</h2>
      {drafts.length === 0 ? (
        <p>No draft bookings yet.</p>
      ) : (
        <ul className="space-y-4">
          {drafts.map((item, index) => (
            <li key={index} className="p-4 border rounded shadow">
              <p><strong>Hotel:</strong> {item.hotelName}</p>
              <p><strong>City:</strong> {item.city}</p>
              <p><strong>Time:</strong> {new Date(item.timestamp).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DraftBookings;
