import React, { useEffect, useState } from 'react';
import HotelCard from './HotelCard';
import hotelData from '../data/hotels.json'; // Replace with your actual path

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCity, setSelectedCity] = useState('All');
  const [selectedStars, setSelectedStars] = useState('All');
  const [showRecommended, setShowRecommended] = useState(false);

  useEffect(() => {
    setHotels(hotelData);
  }, []);

  const getAllCities = () => {
    const cities = hotels.map(hotel => hotel.city);
    return ['All', ...Array.from(new Set(cities))];
  };

  const filteredHotels = hotels.filter(hotel => {
    const hotelName = hotel.hotel_name?.toLowerCase() || '';
    const hotelCity = hotel.city?.toLowerCase() || '';
    const search = searchTerm.toLowerCase();

    const matchesSearch =
      hotelName.includes(search) || hotelCity.includes(search);

    const matchesCity =
      selectedCity === 'All' ||
      hotel.city?.toLowerCase().trim() === selectedCity.toLowerCase().trim();

    const matchesStars =
      selectedStars === 'All' ||
      hotel.star_rating === parseInt(selectedStars);

    const matchesRecommended = !showRecommended || hotel.rating_average >= 8;

    return matchesSearch && matchesCity && matchesStars && matchesRecommended;
  });

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Explore Hotels</h1>

      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          className="border p-2 rounded w-60"
        />

        <select
          value={selectedCity}
          onChange={e => setSelectedCity(e.target.value)}
          className="border p-2 rounded"
        >
          {getAllCities().map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedStars}
          onChange={e => setSelectedStars(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="All">All Stars</option>
          <option value="5">5 Stars</option>
          <option value="4">4 Stars</option>
          <option value="3">3 Stars</option>
          <option value="2">2 Stars</option>
          <option value="1">1 Star</option>
        </select>

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={showRecommended}
            onChange={() => setShowRecommended(!showRecommended)}
          />
          Show Recommended
        </label>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredHotels.map(hotel => (
          <HotelCard key={hotel.hotel_id} hotel={hotel} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
