import React, { useEffect, useState } from "react";
import hotelsData from "../data/hotels.json";
import HotelCard from "./HotelCard";

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [search, setSearch] = useState("");
  const [filterRating, setFilterRating] = useState(0);
  const [filterCity, setFilterCity] = useState("");
  const [visited, setVisited] = useState([]);

  useEffect(() => {
    setHotels(hotelsData);
  }, []);

  const handleVisit = (hotel) => {
    const newVisited = [...visited, hotel];
    setVisited(newVisited);
    localStorage.setItem("visited", JSON.stringify(newVisited));
  };

  const recommended = hotels.filter(
    (hotel) => visited.some((v) => v.city === hotel.city) && hotel.star_rating >= 4
  );

  const filteredHotels = hotels.filter((hotel) => {
    return (
      hotel.hotel_name.toLowerCase().includes(search.toLowerCase()) &&
      hotel.star_rating >= filterRating &&
      (filterCity === "" || hotel.city === filterCity)
    );
  });

  const cities = [...new Set(hotels.map((hotel) => hotel.city))];

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Explore Hotels</h1>

      {/* Search & Filter Section */}
      <div className="flex flex-col md:flex-row items-start md:items-center gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels..."
          className="border p-2 rounded w-full md:w-1/3"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <select
          className="border p-2 rounded"
          value={filterCity}
          onChange={(e) => setFilterCity(e.target.value)}
        >
          <option value="">All Cities</option>
          {cities.map((city, idx) => (
            <option key={idx} value={city}>
              {city}
            </option>
          ))}
        </select>

        <select
          className="border p-2 rounded"
          value={filterRating}
          onChange={(e) => setFilterRating(Number(e.target.value))}
        >
          <option value={0}>All Ratings</option>
          <option value={3}>3★ & above</option>
          <option value={4}>4★ & above</option>
          <option value={5}>5★ only</option>
        </select>
      </div>

      {/* Recommended Section */}
      {recommended.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4 text-purple-700">
            🎯 Recommended for You
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {recommended.map((hotel) => (
              <HotelCard key={hotel.hotel_id} hotel={hotel} onVisit={handleVisit} />
            ))}
          </div>
        </div>
      )}

      {/* Filtered Hotels Section */}
      <div>
        <h2 className="text-2xl font-semibold mb-4">Available Hotels</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredHotels.map((hotel) => (
            <HotelCard key={hotel.hotel_id} hotel={hotel} onVisit={handleVisit} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
