import React, { useEffect, useState } from "react";
import HotelCard from "./HotelCard";
import hotelsData from "../data/hotels.json";

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedStar, setSelectedStar] = useState("All Stars");
  const [showRecommended, setShowRecommended] = useState(false);
  const [filteredHotels, setFilteredHotels] = useState([]);

  useEffect(() => {
    let filtered = [...hotelsData];

    // Search filter
    if (searchTerm.trim()) {
      filtered = filtered.filter(hotel =>
        hotel.hotel_name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // City filter
    if (selectedCity) {
      filtered = filtered.filter(hotel =>
        hotel.city.toLowerCase() === selectedCity.toLowerCase()
      );
    }

    // Star rating filter
    if (selectedStar !== "All Stars") {
      const stars = parseInt(selectedStar);
      filtered = filtered.filter(hotel =>
        Math.round(hotel.star_rating) === stars
      );
    }

    // Recommended filter
    if (showRecommended) {
      filtered = filtered.filter(hotel =>
        hotel.rating_average >= 8
      );
    }

    setFilteredHotels(filtered);
  }, [searchTerm, selectedCity, selectedStar, showRecommended]);

  // Unique city list
  const cities = Array.from(new Set(hotelsData.map(hotel => hotel.city))).sort();

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Explore Hotels</h1>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          className="p-2 border rounded w-60"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />

        <select
          value={selectedCity}
          onChange={e => setSelectedCity(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">All Cities</option>
          {cities.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedStar}
          onChange={e => setSelectedStar(e.target.value)}
          className="p-2 border rounded"
        >
          <option>All Stars</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </select>

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={showRecommended}
            onChange={() => setShowRecommended(prev => !prev)}
          />
          Show Recommended
        </label>
      </div>

      {/* Hotel Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredHotels.map(hotel => (
          <HotelCard key={hotel.hotel_id} hotel={hotel} />
        ))}
        {filteredHotels.length === 0 && (
          <p className="text-gray-500">No hotels match your criteria.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
