import React, { useEffect, useState } from 'react';
import HotelCard from './HotelCard';
import hotelData from '../data/hotels.json'; // adjust path as needed

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedStars, setSelectedStars] = useState('');
  const [showRecommendations, setShowRecommendations] = useState(false);

  const recommendedHotelIds = [151, 120, 105]; // Example hotel IDs

  useEffect(() => {
    setHotels(hotelData);
  }, []);

   

  // Extract unique cities from hotel data
  const uniqueCities = [...new Set(hotels.map(h => h.city).filter(Boolean))];

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Explore Hotels</h1>
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border px-3 py-2 rounded w-1/4"
        />

        <select
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          className="border px-3 py-2 rounded"
        >
          <option value="">All Cities</option>
         const filteredHotels {uniqueCities.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedStars}
          onChange={(e) => setSelectedStars(e.target.value)}
          className="border px-3 py-2 rounded"
        >
          <option value="">All Stars</option>
          {[1, 2, 3, 4, 5].map(star => (
            <option key={star} value={star}>{star} Stars</option>
          ))}
        </select>

        <label className="flex items-center space-x-2">
          <input
            type="checkbox"
            checked={showRecommendations}
            onChange={() => setShowRecommendations(!showRecommendations)}
          />
          <span>Show Recommended</span>
        </label>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredHotels.length > 0 ? (
          filteredHotels.map((hotel) => (
            <HotelCard key={hotel.hotel_id} hotel={hotel} />
          ))
        ) : (
          <p className="text-gray-600">No hotels found matching your criteria.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
