import React, { useState, useEffect } from 'react';
import HotelCard from './HotelCard';
import hotelData from '../data/hotels.json'; // Adjust if JSON is elsewhere

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCity, setSelectedCity] = useState('All');
  const [selectedStars, setSelectedStars] = useState('All');
  const [showRecommended, setShowRecommended] = useState(false);

  useEffect(() => {
    setHotels(hotelData); // Simulate fetching data
  }, []);

  // Extract unique cities
  const cities = ['All', ...new Set(hotelData.map(h => h.city).filter(Boolean))];

  const filteredHotels = hotels.filter(hotel => {
    const matchesSearch =
      hotel.hotel_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      hotel.city.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCity = selectedCity === 'All' || hotel.city === selectedCity;
    const matchesStars = selectedStars === 'All' || hotel.star_rating === parseInt(selectedStars);
    const matchesRecommended = !showRecommended || hotel.rating_average >= 8;

    return matchesSearch && matchesCity && matchesStars && matchesRecommended;
  });

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">Explore Hotels</h2>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search hotels"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          className="border p-2 rounded w-60"
        />

        <select
          value={selectedCity}
          onChange={e => setSelectedCity(e.target.value)}
          className="border p-2 rounded"
        >
          {cities.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <select
          value={selectedStars}
          onChange={e => setSelectedStars(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="All">All Stars</option>
          {[1, 2, 3, 4, 5].map(star => (
            <option key={star} value={star}>{star} Stars</option>
          ))}
        </select>

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={showRecommended}
            onChange={() => setShowRecommended(prev => !prev)}
          />
          Show Recommended
        </label>
      </div>

      {/* Hotel Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredHotels.map(hotel => (
          <HotelCard key={hotel.hotel_id} hotel={hotel} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
