import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import hotels from '../data/hotels.json';

function HotelDetails() {
  const { id } = useParams();
  const [recommendations, setRecommendations] = useState([]);
  const hotel = hotels.find(h => h.id === id);

  useEffect(() => {
    const similar = hotels.filter(h => h.location === hotel.location && h.id !== hotel.id);
    setRecommendations(similar);
  }, [hotel]);

  const logAction = (type) => {
    console.log(`${type} booking for hotel ${hotel.name}`);
  };

  if (!hotel) return <p>Loading...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-2">{hotel.name}</h1>
      <p>üìç {hotel.location}</p>
      <p>‚≠ê {hotel.rating}</p>
      <div className="mt-4 space-x-2">
        <button onClick={() => logAction('draft')} className="px-4 py-2 bg-yellow-400 rounded">Draft Booking</button>
        <button onClick={() => logAction('complete')} className="px-4 py-2 bg-green-500 rounded text-white">Complete Booking</button>
      </div>
      <h2 className="mt-6 text-xl font-semibold">Recommended Hotels:</h2>
      <ul className="list-disc ml-5">
        {recommendations.map(h => (
          <li key={h.id}>{h.name} ({h.location})</li>
        ))}
      </ul>
    </div>
  );
}

export default HotelDetails;
